import React, {Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';

import Rank from './components/Rank/Rank';
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import Clarifai from 'clarifai';

const app = new Clarifai.App({
  apiKey: '81da36648fe246de93ce115dbc8b1964'
 });
class App extends Component {

  constructor() {
    super();
    this.state = {
      input: '',
      imageURL: '',
      box: {},
    }
  }

  calculateFaceLocation= (data) => {
   const image = document.getElementById('inputImage');
   const result = [];
   if (Array.isArray(data)){
     data.forEach((item) => {
       result.push(item.region_info.bounding_box);
     });
   } else {
     this.setState((prev) => ({
      ...prev, hasError: true,
     }));
   }
   const box = [];
   const width = Number(image.width);
   const height = Number(image.height);
   result.forEach((item) => {
     box.push({
      leftCol: item.left_col * width,
      topRow: item.top_row * height,
      rightCol: width - (item.right_col * width),
      bottomRow: height - (item.bottom_row * height)
     });
   });
    return box;
  };

  displayFaceBox = (box) => {
    console.log(box);
    this.setState({box});
  }
  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onButtonSubmit = () => {
    this.setState({imageURL: this.state.input});

    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
    .then(response => this.displayFaceBox(
    this.calculateFaceLocation(response)))
    .catch(err =>
      console.log(err));


  }
  render() {


    const particlesInit = async (main) => {
     console.log(main);
      // you can initialize the tsParticles instance (main) here, adding custom shapes or presets
      // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
      // starting from v2 you can add only the features you need reducing the bundle size
      await loadFull(main);
    };

    const particlesLoaded = (container) => {
      console.log(container);
    };
    return (
      <div className=' App'>
        <div className='particles'>
      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          background: {
            color: {
              value: "linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab)"
            },
          },
          fpsLimit: 120,
          interactivity: {
            events: {
              onClick: {
                enable: true,
                mode: "push",
              },
              onHover: {
                enable: true,
                mode: "repulse",
              },
              resize: true,
            },
            modes: {
              push: {
                quantity: 4,
              },
              repulse: {
                distance: 200,
                duration: 0.4,
              },
            },
          },
          particles: {
            color: {
              value: "#ffffff",
            },
            links: {
              color: "#ffffff",
              distance: 150,
              enable: true,
              opacity: 0.5,
              width: 1,
            },
            collisions: {
              enable: true,
            },
            move: {
              direction: "none",
              enable: true,
              outModes: {
                default: "bounce",
              },
              random: false,
              speed: 1,
              straight: false,
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 80,
            },
            opacity: {
              value: 0.5,
            },
            shape: {
              type: "circle",
            },
            size: {
              value: { min: 1, max: 5 },
            },
          },
          detectRetina: true,
        }}
      />
      </div>

    <Navigation />
    <Logo />
    <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit= {this.onButtonSubmit}/>
    <Rank />
    <FaceRecognition box= {this.state.box} imageURL={this.state.imageURL} />


   </div>


  );
}
}


export default App;
